% --- Executes just before corrections_gui is made visible.
function corrections_gui_OpeningFcn(hObject, eventdata, handles, varargin)
% This function has no output args, see OutputFcn.
% hObject    handle to figure
% eventdata  reserved - to be defined in a future version of MATLAB
% handles    structure with handles and user data (see GUIDATA)
% varargin   command line arguments to corrections_gui (see VARARGIN)

global correction_settings;
handles.correction_settings = correction_settings;

% if nargin>3
%     handles.corrections = varargin{1};    
% else
%     ones = [1 1 1 1 1 1];
%     % default structure of the corrections parameters
%     d.correction_apply = 0*ones; % apply corrections flag
%     d.correction_classes  = 'x'*ones; % class with fixed value
%     d.correction_values = [0.0106 0.0037 1 1 1 1]; % in counts/second
%     handles.corrections = d; %
% end;
% 
% handles.corrections_orig = handles.corrections;

fill_objects(handles);
set(handles.figure1,'Name','Correction & Calculation settings');

%set(handles.figure1,'Name','Apply corrections to variables');
%fpos=get(handles.figure1,'Position');
%set(handles.figure1,'Position',[30 30 fpos(3:4)]);

% Choose default command line output for corrections_gui
handles.output = handles.correction_settings;

handles = update_gui_fontsize(handles);

% Update handles structure
guidata(hObject, handles);

% Make the GUI modal
%set(handles.figure1,'WindowStyle','modal')

% UIWAIT makes corrections_gui wait for user response (see UIRESUME)
%uiwait(handles.figure1);

