% --- Executes on button press in checkbox1.
function checkbox1_Callback(hObject, eventdata, handles)
% hObject    handle to checkbox1 (see GCBO)
% eventdata  reserved - to be defined in a future version of MATLAB
% handles    structure with handles and user data (see GUIDATA)

% Hint: get(hObject,'Value') returns toggle state of checkbox1

function fill_objects(h)
% set(h.checkbox1,'value',h.corrections.correction_apply(1));
% set(h.checkbox2,'value',h.corrections.correction_apply(2));
% set(h.checkbox3,'value',h.corrections.correction_apply(3));
% set(h.checkbox4,'value',h.corrections.correction_apply(4));
% set(h.checkbox5,'value',h.corrections.correction_apply(5));
% set(h.checkbox6,'value',h.corrections.correction_apply(6));

for ii=1:6
    s=sprintf('set(h.checkbox%d,''value'',%d);',ii,h.correction_settings.correction_apply(ii));
    eval(s);
    s=sprintf('set(h.edit%d,''string'',''%s'');',ii,h.correction_settings.correction_classes(ii));
    eval(s);
    txt_old = num2str(h.correction_settings.correction_values(ii));
    s=sprintf('set(h.edit%d,''string'',''%s'');',10+ii,txt_old);
    eval(s);
    s=sprintf('set(h.checkbox%d,''value'',%d);',ii+6,h.correction_settings.calculate_rate(ii));
    eval(s);
    txt_old = num2str(h.correction_settings.medium_value{ii});
    txt_new = regexprep(txt_old,' +',' ');    
    s=sprintf('set(h.edit%d,''string'',''%s'');',20+ii,txt_new);
    eval(s);    
end
txt_old = num2str(h.correction_settings.treatments);
txt_new = regexprep(txt_old,' +',' ');
s=sprintf('set(h.edit%d,''string'',''%s'');',31,txt_new);
eval(s);  
txt_old = num2str(h.correction_settings.incubation_times);
txt_new = regexprep(txt_old,' +',' ');
s=sprintf('set(h.edit%d,''string'',''%s'');',32,txt_new);
eval(s);    


function h = get_values(h)

% h.corrections.correction_apply(1) = get(h.checkbox1,'value');
% h.corrections.correction_apply(2) = get(h.checkbox2,'value');
% h.corrections.correction_apply(3) = get(h.checkbox3,'value');
% h.corrections.correction_apply(4) = get(h.checkbox4,'value');
% h.corrections.correction_apply(5) = get(h.checkbox5,'value');
% h.corrections.correction_apply(6) = get(h.checkbox6,'value');
for ii=1:6
    s=sprintf('d.correction_settings.correction_apply(%d) = get(h.checkbox%d,''value'');',ii,ii);
    eval(s);
    s=sprintf('d.correction_settings.correction_classes(ii) = get(h.edit%d,''string'');',ii);
    eval(s);
    s=sprintf('d.correction_settings.correction_values(ii) = str2num(get(h.edit%d,''string''));',10+ii);
    eval(s);    
    s=sprintf('d.correction_settings.calculate_rate(%d) = get(h.checkbox%d,''value'');',ii,ii+6);
    eval(s);
    s=sprintf('d.correction_settings.medium_value{ii} = str2num(get(h.edit%d,''string''));',20+ii);
    eval(s);
end
s=sprintf('d.correction_settings.treatments = [str2num(get(h.edit%d,''string''))];',31);
eval(s);
s=sprintf('d.correction_settings.incubation_times = [str2num(get(h.edit%d,''string''))];',32);
eval(s);
h.correction_settings = d.correction_settings;
fprintf(1,'Corrections & calculation settings updated.\n');
